import React from 'react'
import '../App.css'
import {Link} from 'react-router-dom'
import axios from "axios";

class Navigataion extends React.Component {
  state = {
    loggedIn:''
  }


  componentDidMount = () => {
      axios.get('/sessions').then(
        (response) => {
          this.setState({
            loggedIn:response.data
          })
        }
      )

  }

  render () {


    return (
      <nav className="nav-bar">
        <Link to="/" className="nav-links">Home </Link >
        { this.state.loggedIn !== '' ? <Link to="/profile" className="nav-links">Profile </Link > : null}
        <Link to="/login" className="nav-links">Login </Link >
        <Link to="/signup" className="nav-links">Signup</Link >
      </nav>
    )
  }
}

export default Navigataion
