import React from 'react'
import axios from "axios";

class Tool extends React.Component {
  state = {
    tools:[]
  }

  componentDidMount = () => {
    const id = event.target.getAttribute('id')
      axios.get('/tools' + id  ).then(
        (response) => {
          this.setState({
              tools:response.data
          })
        }
      )
    }

  render () {
    return (

      <div>
      {
        this.state.tools.map(
          (tool) => {
          return (
            <h1>{tool.title}</h1>
          )
        })
      }
      </div>

    )
  }
}

export default Tool
