import React from 'react'

class Login extends React.Component {
  state = {
    username:'',
    password:''
  }

  createSession = (event) => {
    event.preventDefault();
    axios.post(
        '/tools',
        {
          username:this.state.username,

        }
    ).then(
      (response) => {
        this.setState({
          tools:response.data
        })
      }
    )

  }

  onChangeUsername = (e) =>{
    this.setState({
      username:this.target.value
    })
  }

  onChangePassword = (e) =>{
    this.setState({
      password:this.target.value
    })
  }


  render () {
    return (
      <>
        <h3>Login</h3>
        <div>
          <form onSubmit={this.createSession}>
            <input  type="text" onChange={this.onChangeUsername} placeholder="Username" required/><br/>
            <input  type="password" onChange={this.onChangePassword} placeholder="Password" required/><br/>
            <input type="submit" value="Login"/>

          </form>
        </div>
      </>
    )
  }
}

export default Login
