import React from 'react'
import axios from "axios";
import '../css/Tool.css'
import Navigation from '../components/Navigation.js'

class Tool extends React.Component {
  state = {

  }
  componentDidMount = () => {
        let id = this.props.match.params.id;
        axios.get('/tools/'+id).then(
            response => {
                this.setState({
                  id:response.data.id,
                  title:response.data.title,
                  img:response.data.img,
                  description:response.data.description,
                  price:response.data.price,
                  tags:response.data.tags,
                  rentee:response.data.rentee
                });
            }
        )
    }

  render () {
    return (
      <div className="show-main-container">
        <Navigation />
        <div className="show-container">
        <div>
        <img className="show-tool-img" src={this.state.img}/>
        </div>
        <div>
        <h1>{this.state.title}</h1>
        <h3>Posted by: {this.state.rentee}</h3>
        <hr/>
        <h2>Price per day: ${this.state.price}</h2>
        <h2>description: {this.state.description}</h2>
        <h2>Posted by: {this.state.rentee}</h2>
        <button>Rent Now</button>
        </div>
        </div>
      </div>
    )
  }
}

export default Tool
