import React from 'react'
import axios from "axios";

class Tool extends React.Component {
  state = {
    tools:[]
  }

  componentDidMount = () => {
      axios.get('/tools').then(
        (response) => {
          this.setState({
              tools:response.data
          })
        }
      )
    }

  render () {
    return (

      <div>
      {
        this.state.tools.map((tool) => {
          return ( <h3>{tool.title}</h3> )})
      }
      </div>

    )
  }
}

export default Tool
